ttitle\",\n    cms_category.category_img as \"typelitpic\",\n    cms_category.category_ico as \"typeico\",\n    cms_category.category_path as \"typepath\",\n    cms_category.top_id as \"topid\",\n    cms_category.category_flag as \"typeflag\",\n    cms_category.category_id as \"parentid\",\n    cms_category.category_parent_ids as \"parentids\",\n    cms_category.category_keyword as \"typekeyword\",\n    cms_category.category_descrip as \"typedescrip\",\n    <#if shortSwitch?? && shortSwitch>\n        CONCAT(category_pinyin,\'.html\') AS \"typelink\"\n    <#else>\n        CONCAT(category_path,\'/index.html\') AS \"typelink\"\n    </#if>\n\nFROM cms_content\n    LEFT JOIN cms_category  ON\n    cms_category.id = cms_content.category_id\n    <#--判断是否有自定义模型表-->\n    <#if tableName??>left join ${tableName} on ${tableName}.link_id=cms_content.id</#if>\nWHERE\n    <#--如果是栏目列表页没有文章id所以只取栏目id-->\n    <#if column??&&column.id??&&!id??>\n        cms_category.id=\'${column.id}\' and\n    </#if>\n    <#--根据站点编号查询-->\n    <#if appId??>\n        cms_content.app_id = \'${appId}\' and\n    </#if>\n    cms_content.del=0\n    <#if id??> and cms_content.id=\'${id}\'</#if>\n', `tag_class` = NULL, `tag_description` = '文章内容', `MODEL_ID` = NULL, `UPDATE_BY` = NULL, `UPDATE_DATE` = NULL, `CREATE_BY` = NULL, `CREATE_DATE` = NULL, `APP_ID` = 'global', `DEL` = 0, `NOT_DEL` = 1 WHERE `id` = 7;
