mapper.update:
update cms_article
ARTICLE_CONTENT=#{articleContent}, ARTICLE_AUTHOR=#{articleAuthor}, ARTICLE_TYPE=#{articleType}, ARTICLE_SOURCE=#{articleSource}, ARTICLE_URL=#{articleUrl}, ARTICLE_KEYWORD=#{articleKeyword}, ARTICLE_FREEORDER=#{articleFreeOrder}, ARTICLE_WEBID=#{articleWebId},
where ARTICLE_BASICID = #{basicId}

mapper.select:
select
,c.CATEGORY_TITLE,cl.column_path,cl.column_type,c.CATEGORY_id,cl.COLUMN_CONTENTMODELID FROM basic b LEFT JOIN category c ON b.BASIC_CATEGORYID = c.CATEGORY_ID LEFT JOIN cms_column cl ON c.CATEGORY_ID = cl.COLUMN_CATEGORYID LEFT JOIN cms_article a ON a.ARTICLE_BASICID = b.BASIC_ID
where a.ARTICLE_BASICID=#{basicId};

select count(basic_id) FROM basic b LEFT JOIN category c ON b.BASIC_CATEGORYID = c.CATEGORY_ID LEFT JOIN cms_column cl ON c.CATEGORY_ID = cl.COLUMN_CATEGORYID LEFT JOIN cms_article a ON a.ARTICLE_BASICID = b.BASIC_ID
a.ARTICLE_WEBID = #{webId}
and b.BASIC_TITLE like CONCAT('%',#{article.basicTitle},'%')
and b.BASIC_CATEGORYID in
#{categoryId}
and a.ARTICLE_TYPE like CONCAT('%',#{flag},'%') and a.ARTICLE_TYPE not like CONCAT('%',#{noFlag},'%')

select count(*) from cms_article a left join basic b on b.basic_id=a.ARTICLE_BASICID where a.ARTICLE_WEBID = #{webId}
and b.BASIC_CATEGORYID in
#{id}
and a.ARTICLE_TYPE like CONCAT('%',#{flag},'%') and a.ARTICLE_TYPE not like CONCAT('%',#{noFlag},'%')

select
,c.CATEGORY_TITLE,cl.column_path,c.CATEGORY_id,cl.COLUMN_CONTENTMODELID,cl.column_type FROM basic b LEFT JOIN category c ON b.BASIC_CATEGORYID = c.CATEGORY_ID LEFT JOIN cms_column cl ON c.CATEGORY_ID = cl.COLUMN_CATEGORYID LEFT JOIN cms_article a ON a.ARTICLE_BASICID = b.BASIC_ID where a.ARTICLE_WEBID = #{webId}
and b.BASIC_ID in
#{id}
and a.ARTICLE_TYPE like CONCAT('%',#{flag},'%') and a.ARTICLE_TYPE not like CONCAT('%',#{noFlag},'%') order by ${orderBy}
order by b.basic_id
desc asc limit #{begin},#{count};

